import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View } from 'react-native';
import Geolocation from '@react-native-community/geolocation';
import axios from 'axios';

export default function App() {
  const [userLocation, setUserLocation] = useState(null);

  useEffect(() => {
    Geolocation.getCurrentPosition(
      position => {
        const { latitude, longitud } = position.coords;
        setUserLocation({ latitude, longitud });
      },
      error => console.log(error),
      { enableHighAccuracy: true, timeout: 20000, maximumAge: 1000 }
    );
  }, []);
  const [data, setData] = useState(null);

  useEffect(() => {
    axios.get('https://analisi.transparenciacatalunya.cat/resource/rhpv-yr4f.json')
      .then(response => setData(response.data))
      .catch(error => console.error(error));
  }, []);
  const filteredData = filterByTerminated(filterByDistance(data, userLocation, distance));
  return (
    <View style={styles.container}>
      {filteredData && filteredData.length > 0 ? (
        filteredData.map(item => (
          <View key={item.codi}>
            <Text>{item.denominaci}</Text>
          </View>
        ))
      ) : (
        <Text>Loading...</Text>
      )}
    </View>
  );
}

const EARTH_RADIUS = 6371; // Radio de la Tierra en km
const distance=10;

const filterByDistance = (data, userLocation, distance) => {
  if (!data) {
    return [];
  }
  return data.filter(item => {
    const dLat = deg2rad(item.lat - userLocation.latitud);
    const dLon = deg2rad(item.lon - userLocation.longitud);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(deg2rad(userLocation.latitude)) *
        Math.cos(deg2rad(item.lat)) *
        Math.sin(dLon / 2) *
        Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    const d = EARTH_RADIUS * c; // Distancia en km
    return d <= distance;
  });
};

const deg2rad = deg => {
  return deg * (Math.PI / 180);
};

const filterByTerminated = data => {
  return data.filter(item => {
    return item.data_fi !== null;
  });
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
