import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View } from 'react-native';
import Geolocation from '@react-native-community/geolocation';
import axios from 'axios';

export default function ApiExample() {
  const [data, setData] = useState([]);

  const filterByDistanceAndDate = (list) => {
    const userCoords = { lat: 41.3887901, lon: 2.1589899 }; // Replace with user's coordinates
    const maxDistanceKm = 3000;
    const currentDate = new Date().toISOString;
    const filteredList = list.filter((item) => {
      const itemCoords = { lat: parseFloat(item.latitud), lon: parseFloat(item.longitud) };
      const distanceKm = getDistance(userCoords, itemCoords);
      const itemDate = new Date(item.fi_data).toISOString;
      return distanceKm <= maxDistanceKm && itemDate <= currentDate;
    });
    return filteredList;
  };

  const getDistance = (start, end) => {
    const deg2rad = (deg) => deg * (Math.PI / 180);
    const R = 6371; // Earth's radius in km
    const dLat = deg2rad(end.lat - start.lat);
    const dLon = deg2rad(end.lon - start.lon);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(deg2rad(start.lat)) *
        Math.cos(deg2rad(end.lat)) *
        Math.sin(dLon / 2) *
        Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    const d = R * c;
    console.log(d);
    return d;
  };

  const filterByDistance = (list) => {
    const filteredList = filterByDistanceAndDate(list);
    setData(filteredList);
  };

  useEffect(() => {
    Geolocation.getCurrentPosition(
      (position) => {
        const { latitude, longitude } = position.coords;
        axios
          .get(
            `https://analisi.transparenciacatalunya.cat/resource/rhpv-yr4f.json?$where=latitud > ${latitude - 0.1} AND latitud < ${latitude + 0.1} AND longitud > ${longitude - 0.1} AND longitud < ${longitude + 0.1}`
          )
          .then((response) => filterByDistance(response.data))
          .catch((error) => console.log(error));
      },
      (error) => console.log(error),
      { enableHighAccuracy: true, timeout: 20000, maximumAge: 1000 }
    );
  }, []);

  return (
    <View style={styles.container}>
      {data.map((item) => (
        console.log("hola"),
        <Text key={item.codi}>{item.denominaci}</Text>
      ))}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
