import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View } from 'react-native';
import axios from 'axios';
import firebase from 'firebase/app';
import 'firebase/database';

const firebaseConfig = {
  apiKey: "AIzaSyDCqz54iHF5AAWBDXNQOKgw2p8WdbhyITA",
  authDomain: "nearvents-react.firebaseapp.com",
  projectId: "nearvents-react",
  storageBucket: "nearvents-react.appspot.com",
  messagingSenderId: "73445885890",
  appId: "1:73445885890:web:c4ca7e5b12e15edcff785b"
};

firebase.initializeApp(firebaseConfig);
const db = firebase.database();

export default function App() {
  const [actesCulturals, setActesCulturals] = useState([]);

  const filterByDistance = (data) => {
    const currentLocation = { lat: 41.38879, lon: 2.15899 }; // coordenadas de ejemplo
    const filteredData = data.filter((acteCultural) => {
      if (
        acteCultural.latitud === null ||
        acteCultural.longitud === null ||
        acteCultural.fi_data < new Date().toISOString().slice(0, 10)
      ) {
        return false;
      }
      const distance = getDistance(
        currentLocation,
        { lat: parseFloat(acteCultural.latitud), lon: parseFloat(acteCultural.longitud) }
      );
      return distance <= 10000000;
    });
    return filteredData;
  };

  const getDistance = (start, end) => {
    const deg2rad = (deg) => deg * (Math.PI / 180);
    const R = 6371; // Earth's radius in km
    const dLat = deg2rad(end.lat - start.lat);
    const dLon = deg2rad(end.lon - start.lon);
    const a =
      Math.sin(dLat / 2) * Math.sin(dLat / 2) +
      Math.cos(deg2rad(start.lat)) *
        Math.cos(deg2rad(end.lat)) *
        Math.sin(dLon / 2) *
        Math.sin(dLon / 2);
    const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    const d = R * c;
    return d;
  };

  useEffect(() => {
    const currentDate = new Date();
    const formattedDate = currentDate.toISOString().substring(0, 10);
    axios
      .get('https://analisi.transparenciacatalunya.cat/resource/rhpv-yr4f.json?$where=data_fi>%27'+formattedDate+'%27')
      .then((response) => {
        const filteredData = filterByDistance(response.data);
        setActesCulturals(filteredData.map((acteCultural) => acteCultural.denominaci));

        // Guarda los datos en Firebase
        db.ref('actesCulturals').set(filteredData);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <View style={styles.container}>
      {actesCulturals.map((acteCultural, index) => (
        <Text key={index}>{acteCultural}</Text>
      ))}
    </View>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
